    23→/**
    24→ * Worker applies for a job
    25→ * Creates a new booking with status 'pending'
    26→ */
    27→export async function applyForJob(jobId: string, workerId: string): Promise<ApplicationResult> {
    28→  try {
    29→    const supabase = await createClient()
    30→
    31→    // First, check if worker has already applied for this job
    32→    const { data: existingApplication, error: checkError } = await supabase
    33→      .from("bookings")
    34→      .select("*")
    35→      .eq("job_id", jobId)
    36→      .eq("worker_id", workerId)
    37→      .single()
    38→
    39→    if (checkError && checkError.code !== "PGRST116") {
    40→      // PGRST116 = not found, which is expected for new applications
    41→      return { success: false, error: "Gagal mengecek status lamaran" }
    42→    }
    43→
    44→    if (existingApplication) {
    45→      return { success: false, error: "Anda sudah melamar untuk pekerjaan ini" }
    46→    }
    47→
    48→    // Get job details to fetch business_id
    49→    const { data: job, error: jobError } = await supabase
    50→      .from("jobs")
    51→      .select("business_id")
    52→      .eq("id", jobId)
    53→      .single()
    54→
    55→    if (jobError || !job) {
    56→      return { success: false, error: "Pekerjaan tidak ditemukan" }
    57→    }
    58→
    59→    // Create the booking (application)
    60→    const newBooking: BookingInsert = {
    61→      job_id: jobId,
    62→      worker_id: workerId,
    63→      business_id: job.business_id,
    64→      status: "pending",
    65→      start_date: new Date().toISOString(),
    66→      end_date: new Date().toISOString(),
    67→      final_price: 0,
    68→    }
    69→
    70→    const { data, error } = await supabase
    71→      .from("bookings")
    72→      .insert(newBooking)
    73→      .select()
    74→      .single()
    75→
    76→    if (error) {
    77→      return { success: false, error: `Gagal melamar: ${error.message}` }
    78→    }
    79→
    80→    return { success: true, data }
    81→  } catch (error) {
    82→    return { success: false, error: "Terjadi kesalahan saat melamar pekerjaan" }
    83→  }
    84→}
