    10→// Type for inserting a new booking (application)
    11→type BookingInsert = Pick<Booking, 'job_id' | 'worker_id' | 'business_id' | 'status' | 'start_date' | 'end_date' | 'final_price'>
    12→
    13→export type ApplicationResult = {
    14→  success: boolean
