    24→/**
    25→ * Worker applies for a job
    26→ * Creates a new booking with status 'pending'
    27→ */
    28→export async function applyForJob(jobId: string, workerId: string): Promise<ApplicationResult> {
    29→  try {
    30→    const supabase = await createClient()
    31→
    32→    // First, check if worker has already applied for this job
    33→    const { data: existingApplication, error: checkError } = await supabase
    34→      .from("bookings")
    35→      .select("*")
    36→      .eq("job_id", jobId)
    37→      .eq("worker_id", workerId)
    38→      .single()
    39→
    40→    if (checkError && checkError.code !== "PGRST116") {
    41→      // PGRST116 = not found, which is expected for new applications
    42→      return { success: false, error: "Gagal mengecek status lamaran" }
    43→    }
    44→
    45→    if (existingApplication) {
    46→      return { success: false, error: "Anda sudah melamar untuk pekerjaan ini" }
    47→    }
    48→
    49→    // Get job details to fetch business_id
    50→    const { data: job, error: jobError } = await supabase
    51→      .from("jobs")
    52→      .select("business_id")
    53→      .eq("id", jobId)
    54→      .single()
    55→
    56→    if (jobError || !job) {
    57→      return { success: false, error: "Pekerjaan tidak ditemukan" }
    58→    }
    59→
    60→    // Create the booking (application)
    61→    const newBooking: BookingInsert = {
    62→      job_id: jobId,
    63→      worker_id: workerId,
    64→      business_id: job.business_id,
    65→      status: "pending",
    66→      start_date: new Date().toISOString(),
    67→      end_date: new Date().toISOString(),
    68→      final_price: 0,
    69→    }
    70→
    71→    const { data, error } = await supabase
    72→      .from("bookings")
    73→      .insert(newBooking)
    74→      .select()
    75→      .single()
    76→
    77→    if (error) {
    78→      return { success: false, error: `Gagal melamar: ${error.message}` }
    79→    }
    80→
    81→    return { success: true, data }
    82→  } catch (error) {
    83→    return { success: false, error: "Terjadi kesalahan saat melamar pekerjaan" }
    84→  }
    85→}
