    91→export async function cancelApplication(bookingId: string, workerId: string): Promise<ApplicationResult> {
    92→  try {
    93→    const supabase = await createClient()
    94→
    95→    // Verify the booking belongs to the worker and is pending
    96→    const { data: booking, error: fetchError } = await supabase
    97→      .from("bookings")
    98→      .select("*")
    99→      .eq("id", bookingId)
   100→      .eq("worker_id", workerId)
   101→      .single()
